INSERT INTO `wt_dept_schedule` VALUES ('0f576512af9845c393f2ad0e67819646', '0153c5ed47f64378a376a20f4b6ea77c', 'c07adaddacf8425e88c0e53178172429', '1a2bcca8a8314f6cafa7031185ca0016', '100', '根据镇政府工作布置，镇农林水务局组织开展了全镇养猪场清理专项工作：制定方案，落实分工；实施网格化监管，并与各社区签订了《厚街镇清理畜禽养殖业污染工作责任书》，建立工作责任制；通过社区自查和我局人员巡查，明确工作任务；督促社区切实开展好清理工作，防止反弹；加强部门协调，及时将巡查中掌握的场点信息书面告知镇环保分局、综合执法分局和有关社区。至2016年底，全镇非法养猪场已基本完成清理。\r\n', '继续加强监管，积极配合环保部门做好我镇非法畜禽养殖业清理。\r\n', '暂无\r\n', '完成', null, null, '2017-03-01 09:08:27', 'ADMIN', '2017', '1');
