INSERT INTO `wt_dept_schedule` VALUES ('146e0137feff4c75a8a75646ae386820', '201602056', '23ddf4832308aaa384319379b1440087', '1a2bcca8a8314f6cafa7031185ca0069', '100', '1.按照万卓培书记2月份召开的公安工作调研会议和全镇公安工作会议精神，2月份，召开治安视频、卡口建设冲刺会，推进视频建设，现平台已接入二类视频点3400个。\r\n2.视频侦查辅助系统已建设完毕并投入使用，目前已通过市局专家组验收。\r\n3.1-12月份，全镇违法犯罪警情同比下降22%，“两抢一盗”警情同比下降15.7%。\r\n', '1.按照计划开展视频监控补点扩面和社区视频、卡口接入工作。\r\n2.进一步整合业务系统资源，加强研判队伍建设，确保视频侦查辅助系统应用成效。\r\n3.进一步加大打击违法犯罪力度。\r\n', '暂无\r\n', '完成', '附件2：2016年镇委、镇政府主要目标任务1-12月工作进展情况表.pdf', '/20170116/838a6f2331244be0bdfd43ff520f9a55.pdf', '2017-01-18 09:41:00', '方志斌', '2016', '12');
