INSERT INTO `wt_dept_schedule` VALUES ('1c62afa995534a7492b8ab70ac5c6a0e', '201603147', '23ddf483230840cccc319379b1440147', '1a2bcca8a8314f6cafa7031185ca0035', '100', '1、完成编制厚街镇家具制造及制鞋行业VOCs污染整治工作方案。\r\n2、完成排查摸底，整理整治名录。\r\n3、完成全镇整治工作动员会议的召开。\r\n4、初步完成《推进我镇家具及制鞋企业VOCs治理技术提升工作方案》及《提升类重点企业名单》的确定。\r\n5、累计完成提升类企业提升改造130家、核定VOCs年排放量小\r\n', '加强对企业的后续监管，确保企业依法守法经营。\r\n', '无\r\n', '正常', null, null, '2017-01-05 16:25:26', '陈茂叶', '2016', '12');
