INSERT INTO `wt_dept_schedule` VALUES ('39fedc54543244ccbf18394a66678e14', '201603012', '23ddf483230840cccc319379b1440012', '1a2bcca8a8314f6cafa7031185ca0004', '100', '①组织了镇监察室、审计办全体干部，社区纪检委员、镇委派社区会计，部分单位党组织的纪检委员共约80人到省委党校进行培训；②镇机关作风暗访片已由东莞市电视台完成了拍摄并完成了最终定稿制作工作；③2016年5月14日联合镇宣传教育文体局等部门举办第八届廉洁文化中小学生现场书画大赛并完成大赛获奖作品集的印刷派送工作；5月15日联合镇妇联开展“树廉洁家风，建最美家庭”主体活动；④6月18日组织纪委全体人员观看廉政话剧《韩文公》；⑤7月13日召开厚街镇第十四期党员干部“三纪”教育辅导报告会暨二十期厚街学习论坛；⑥完成反腐倡廉教育学习资料和电教片的征订和派发工作；⑦联合镇宣教文体局等部门举办“纪念中国共产党建党95周年厚街书法美术摄影作品展”和“厚街镇纪念中国工农红军长征胜利80周年图片展”；⑧邀请市检察院换届纪律宣讲团成员为我镇有关人员开展换届选举预防职务犯罪专题教育；⑨撰写《厚街镇2016年开展纪律教育学习月活动的方案》并以镇委办文件下发至全镇各部门单位、各社区，目前正按方案要求完成纪律教育学习月活动；⑩协调镇有关部门组织1名人员报名参加“修身齐家”演讲选拔赛；⑪完成东莞市2016年廉洁读书月活动并将活动总结情况报送市文广新局。\r\n', '无。\r\n', '无。\r\n', '完成', null, null, '2017-01-13 18:58:19', '方友和', '2016', '12');
