INSERT INTO `wt_dept_schedule` VALUES ('83896ba4795d4ec0acbfbf7be5ee178e', '201602017', '23ddf4832308aaa384319379b1440029', '1a2bcca8a8314f6cafa7031185ca0010', '100', '根据《2016 年市镇两级领导挂点服务大型骨干企业工作方案》及镇领导对亲企走访服务活动工作的批示，8月中旬以来，我镇分别由经信局、商务局牵头，采取“政府、部门、社区”三级联动走访服务机制，启动了新一轮亲企走访大型服务活动，对全镇600家企业（300家外资、300家民营）进行挂点服务和联系走访，本次走访共收集企业问题或困难263个（主要涉及经营成本、人才招引、营商环境、融资贷款、子女入学、市场推广等问题），到目前为止协调解决102个，领导挂点服务企业活动成效显著；另一方面联合镇商务局将本次走访情况录入“千干扶千企”系统，并由各副局长、民营办按照企业反映的情况进行跟进落实，确保事事有反馈。\r\n', '按照企业反映的情况进行跟进落实，确保事事有反馈；同时，进一步健全、完善“千干扶千企”平台作用，及时将集中走访过程中企业反馈问题在平台上反馈、限时解决，提升服务效能。\r\n', '无\r\n', '完成', null, null, '2017-01-13 17:11:12', 'ADMIN', '2016', '12');
