INSERT INTO `wt_dept_schedule` VALUES ('8b152abe01c140b287dbaecb326882e7', '201603130', '23ddf483230840cccc319379b1440130', '1a2bcca8a8314f6cafa7031185ca0038', '0', '1、工业城消防站：已完成填土工程、桩基施工、桩基检测、三层主体结构等。2、大迳自来水管改造工程：完成工程量约88%。3、穗莞深轨道交通项目拆迁建筑回建基础工程：正加紧办理验收手续，争取11月底前完成验收。4、黑水陂河连通渠至出水口拓宽工程：已完成总工程量的约92.0%，2#、4#桥涵准备施工。 5、神仙水公园配套工程：正进行南北入口牌坊施工，完成总工程量约90%。6、沙溪水库休闲亲水绿道工程（一期）：正进行绿道路基及水泥罩面建设；驿站的土建已基本完成，转入外部装修阶段，已完成工程55%。7、松山公园改造工程：正对围墙、树池、广场等进行施工，完成总工程量50%。8、厚沙东路东环路及省道中心区段景观绿化工程：已完成厚沙东东环路绿化种植，正进行人行道铺砖，完成工程量75%。9、永宁园墓区重建工程：施工单位进场施工，正进行砌挡土墙，完成工程量80%。10、涌口社区卫生服务站：已完成招标，计划11月底前开工。', '1、工业城消防站：完成主体工程量60%。2、大迳自来水管改造工程：竣工验收。3、穗莞深轨道交通项目拆迁建筑回建基础工程：做好验收及移交工作。 4、黑水陂河连通渠至出水口拓宽工程：完成工程量的95%。5、神仙水公园配套工程：尽快完工。6、沙溪水库休闲亲水绿道工程（一期）：完成绿道路基及沥青罩面建设；完成驿站的外部装修、停车场施工. 7、松山公园改造工程：计划于农历年前完成围墙、树池、广场及休息亭的墙面铺设，计划完成总工程量70%。8、厚沙东路东环路及省道中心区段景观绿化工程：进行省道景观铺垫层。9、永宁园墓区重建工程：完成园建、建筑物、洗手间、亭。10、涌口社区卫生服务站：按计划进场施工。', '暂无', '正常', null, null, '2017-01-05 15:54:09', '方永恒', '2016', '12');
