INSERT INTO `wt_dept_schedule` VALUES ('9544bbb6fb154d74a8ba705b4b7c828d', 'b086af40fc05452e8f87adb4d5d700de', 'c70e367c42ef4b809b2f1825f467372c', '1a2bcca8a8314f6cafa7031185ca0016', '80', '根据《东莞市河道堤防管理规定》等规定对洗砂场未经许可占用河道管理范围、未经许可从河道取水等违法行为进行书面发函和口头说明，要求洗砂场针必须退出东江南支流河道管理范围之外以及禁止未经许可擅自从河道取水；同时，镇农林水务局、水务建运中心向市水务局、市水政监察支队申请执法业务指导。\r\n', '继续加强巡查，告知各砂场按规定办理临时占用河道及取水许可审批。\r\n', '巡查执法难度大，存在偷偷复工行为。\r\n', '完成', null, null, '2017-03-01 09:10:31', 'ADMIN', '2017', '1');
