INSERT INTO `wt_dept_schedule` VALUES ('a6d18a125e5942ebaad3c8b076ab0c40', '201603015', '23ddf483230840cccc319379b1440015', '1a2bcca8a8314f6cafa7031185ca0004', '100', '①1月下旬更换位于镇府大院电梯两旁的部分反腐倡廉宣传广告，5月下旬将位于镇府大院电梯两旁的部分反腐倡廉宣传广告更换为主题为“廉洁选举”、“两学一做”、“预防职务犯罪”的宣传海报广告，8月-9月将位于镇府大院电梯两旁的部分反腐倡廉宣传广告更换为主题为纪律教育学习月活动宣传海报广告；②截至12月30日向全镇党员干部发送预防职务犯罪及党风廉政警示宣传短信约21000条；③2月下旬，征订《习近平关于严明党的纪律和规矩论述摘编》并分发至各社区和部分部门单位；④经与镇有关部门协调，将位于高速公路口博览大道边的户外公益及反腐倡廉宣传广告调整至高速路的另外一侧，广告牌数量由原来2块增加到3块并进行宣传内容更换，同时在厚街广场和体育公园新设3块党风廉政建设和预防职务犯罪宣传广告牌用于加强宣传工作，并定期对广告进行更换。\r\n', '无\r\n', '无\r\n', '完成', null, null, '2017-01-13 19:06:09', '方友和', '2016', '12');
