INSERT INTO `wt_dept_schedule` VALUES ('bd6a9f309e954534ad8e83bac7592d5c', '201602050', '23ddf4832308aaa384319379b1440077', '1a2bcca8a8314f6cafa7031185ca0046', '100', '我分局制定横岗水库周边环境整治方案，执法人员对横岗水库周边的在建违法建筑、“六乱”情况进行调查摸底，并对违建情况逐一登记造册。在巡查过程中，一旦发现“六乱”现象，及时查处。同时，联合属地社区重点对横岗水库周边的在建违法建筑、城市“六乱”等违法行为开展专项整治工作，全年共查处28宗乱摆卖违法行为。\r\n', '继续加强横岗水库周边城市综合管理专项整治工作。\r\n', '无\r\n', '完成', null, null, '2017-01-12 17:58:01', '方志斌', '2016', '12');
