INSERT INTO `wt_dept_schedule` VALUES ('c34df8c95d7d4019a53eb9628414bc76', 'b086af40fc05452e8f87adb4d5d700de', '8c9966af8a0f43ddbe56281e1436c565', '1a2bcca8a8314f6cafa7031185ca0058', '100', '为规范凯伦花园周边相关经营户的经营行为，有效整治凯伦花园周边环境污染，我分局出动执法人员16人次，检查了位于石角码头附近的沙石场9个，经核实其中8个沙场均已领取营业执照，另有1艘存放石灰的船只，属流动性质，已停业，未发现存在违反工商法律法规的违法经营行为；检查凯伦花园周边（含石角码头）经营建筑材料的经营户21户，已领取营业执照 19户，无照经营2户，已对2家无照经营经营建筑材料的经营者依法立案查处。\r\n', '将对该区域加强巡查监管，加强对该区域经营户的经营行为的规范，严厉打击无照经营建筑材料行为。\r\n', '无\r\n', '完成', null, null, '2017-03-01 09:09:26', 'ADMIN', '2017', '1');
