INSERT INTO `wt_dept_task` VALUES ('23ddf483230840cccc319379b1440134', '201603134', '1a2bcca8a8314f6cafa7031185ca0042', '1、确保顺利完成社区集体资产网上交易的有关任务；\n2、结合实际需要，对各社区的社区集体资产网上交易工作进行指导，确保村级交易点顺利开展网上交易。', '2016-12-01 00:00:01', '2016-12-31 23:59:59', '2016-12-01 00:00:01', '2016-12-31 23:59:59', null, 'Y', '0', '100', 'N', null, null, 'N', '2016-12-01 08:00:00', '\"1.已完成镇和各社区网上交易投标保证金账户的开设，并录入网上交易系统；与镇社资办多次召集农业银行、金宇恒软件公司进行讨论，并联合草拟了网上交易工作方案，明确各相关部门职责分工；\r\n2.多次参加由市农资办举办的网上交易培训课程，熟练交易系统操作，掌握有关问题处理方法；\r\n3.2016年已完成网上交易19宗，已挂网流标的48宗。\"\r\n\r\n', '无\r\n', '暂无\r\n', '完成', null, null, '2017-01-17 16:12:42', '方楚怀', 'O', 'N', 'O', null, null, null, '方楚怀', '2016', '12');
