INSERT INTO `wt_dept_task` VALUES ('23ddf483230840cccc319379b1440147', '201603147', '1a2bcca8a8314f6cafa7031185ca0035', '根据《东莞市臭氧污染防控专项行动计划（2015-2017）》（东府办〔2015〕131号）和《东莞市家具制造及制鞋行业VOCs污染整治工作实施方案》有关要求，按“提升一批、整治一批、淘汰一批”原则，制定全镇家具制造及制鞋行业VOCs整治方案，到2016年底，全面完成家具制造及制鞋行业VOCs污染整治，建立污染防治长效管理机制，VOCs排放总量进一步下降，臭氧污染现状明显改善。', '2016-12-01 00:00:01', '2016-12-31 23:59:59', '2016-12-01 00:00:01', '2016-12-31 23:59:59', null, 'Y', '0', '100', 'N', null, null, 'N', '2016-12-01 08:00:00', '1、完成编制厚街镇家具制造及制鞋行业VOCs污染整治工作方案。\r\n2、完成排查摸底，整理整治名录。\r\n3、完成全镇整治工作动员会议的召开。\r\n4、初步完成《推进我镇家具及制鞋企业VOCs治理技术提升工作方案》及《提升类重点企业名单》的确定。\r\n5、累计完成提升类企业提升改造130家、核定VOCs年排放量小\r\n', '加强对企业的后续监管，确保企业依法守法经营。\r\n', '无\r\n', '正常', null, null, '2017-01-05 16:25:26', '陈茂叶', 'O', 'N', 'O', null, null, null, '黄锡彬', '2016', '12');
