INSERT INTO `wt_dept_task` VALUES ('23ddf483230840cccc319379b1440175', '201603175', '1a2bcca8a8314f6cafa7031185ca0066', '确保“三小”场所无违规住人现象，“三小”场所、出租屋无重大火灾隐患，形成消防安全监管长效机制。', '2016-12-01 00:00:01', '2016-12-31 23:59:59', '2016-12-01 00:00:01', '2016-12-31 23:59:59', null, 'Y', '0', '100', 'N', null, null, 'N', '2016-12-01 08:00:00', '组织人员对各社区“三小”场所进行不间断抽查，重点做好违规住人场所的督察、跟踪、整改工作；今年以来，累计抽查“三小”场所8455间，清查违规住人及回潮401间；抽查出租屋411栋，整改存在问题出租屋89栋。\r\n', '深入推进智网工程，集中开展“三小”场所和出租屋专项整治行动，不间断开展对“三小”场所、出租屋的监督检查，及时消除隐患。\r\n', '暂无\r\n', '正常', null, null, '2017-01-11 15:41:59', '邱壹鸣', 'O', 'N', 'O', null, null, null, '黄定华', '2016', '12');
