INSERT INTO `wt_dept_task` VALUES ('b59c60393a694349be5f729129542676', '1fe2a5bfc0284e2e90ea1d19412aa66a', '1a2bcca8a8314f6cafa7031185ca0083', null, '2017-01-01 09:40:00', '2017-12-31 09:40:00', '2017-01-01 09:40:00', '2017-12-31 09:40:00', null, 'Y', '0', '100', 'N', null, null, 'N', '2017-01-16 09:42:07', '镇环保分局、卫生部门联合新围社区于2017年1月13日进行对马贝石一处医疗废物临时存放处进行检查整治，经检查此处不符合存放医疗废物条件。并要求业主搬迁至符合环保条件的场所存放处理，业主已按要求搬迁。\r\n', '现由镇环保分局监督整治。\r\n', '无\r\n', '正常', null, null, '2017-01-16 16:41:31', '钟燕辉', 'O', 'Y', 'O', null, null, null, null, '2017', '1');
INSERT INTO `wt_dept_task` VALUES ('c07adaddacf8425e88c0e53178172429', '0153c5ed47f64378a376a20f4b6ea77c', '1a2bcca8a8314f6cafa7031185ca0016', null, '2017-01-01 09:44:00', '2017-12-31 09:44:00', '2017-01-01 09:44:00', '2017-12-31 09:44:00', null, 'Y', '0', '100', 'N', null, null, 'N', '2017-01-16 09:45:11', '根据镇政府工作布置，镇农林水务局组织开展了全镇养猪场清理专项工作：制定方案，落实分工；实施网格化监管，并与各社区签订了《厚街镇清理畜禽养殖业污染工作责任书》，建立工作责任制；通过社区自查和我局人员巡查，明确工作任务；督促社区切实开展好清理工作，防止反弹；加强部门协调，及时将巡查中掌握的场点信息书面告知镇环保分局、综合执法分局和有关社区。至2016年底，全镇非法养猪场已基本完成清理。\r\n', '继续加强监管，积极配合环保部门做好我镇非法畜禽养殖业清理。\r\n', '暂无\r\n', '完成', null, null, '2017-03-01 09:08:27', 'ADMIN', 'O', 'Y', 'O', null, null, null, null, '2017', '1');
INSERT INTO `wt_dept_task` VALUES ('c70e367c42ef4b809b2f1825f467372c', 'b086af40fc05452e8f87adb4d5d700de', '1a2bcca8a8314f6cafa7031185ca0016', null, '2017-01-01 09:55:00', '2017-12-31 09:56:00', '2017-01-01 09:55:00', '2017-12-31 09:56:00', null, 'Y', '0', '80', 'N', null, null, 'N', '2017-01-16 10:02:19', '根据《东莞市河道堤防管理规定》等规定对洗砂场未经许可占用河道管理范围、未经许可从河道取水等违法行为进行书面发函和口头说明，要求洗砂场针必须退出东江南支流河道管理范围之外以及禁止未经许可擅自从河道取水；同时，镇农林水务局、水务建运中心向市水务局、市水政监察支队申请执法业务指导。\r\n', '继续加强巡查，告知各砂场按规定办理临时占用河道及取水许可审批。\r\n', '巡查执法难度大，存在偷偷复工行为。\r\n', '完成', null, null, '2017-03-01 09:10:31', 'ADMIN', 'O', 'Y', 'O', null, null, null, null, '2017', '1');
