INSERT INTO `wt_task_execution` VALUES ('3b394c106f544e6b96e92b59834ae5af', '201603017', '3c9d9297ddc14789a245ed3119067457', '319528c74cd44013a3573995fe0c0135', '2016-12-30 18:32:25', '①3月中旬市农业局转来《关于征求<关于做好第六届农村（社区）干部任期经济责任审计工作的通知>意见的函》（东农函[2016]45号），我办经与多部门研究分析后，提出我办的意见反馈市农业局；②5月24日收到《东莞市人民政府关于做好第六届农村（社区）干部任期经济责任审计工作的通知》（东府办函[2016]228号）文件，将根据此件精神，结合我镇实际，制定我镇第六届农村（社区）干部任期经济责任审计工作方案；③5月31日上午，我办3名同志参加市农资办召开的农村（社区）干部任期经济责任审计培训会议；④制定厚街镇第六届社区干部任期经济责任审计工作方案，报镇领导审批；批复后以镇人民政府办公室名义下发此方案；⑤制定每个被审单位的实施方案；⑥6月20日召开全体审计工作人员会议，布置审计任务、分工和时间安排，落实责任，及进行业务解讲；⑦6月21日向被审单位下达审计通知书，并要求被审单位准备相关资料；⑧6月27-30日与各被审单位分别召开审计座谈会；⑨7月份进驻社区实施就地审计，进行审计取证；⑩7月22日召开各审计小组成员座谈会，了解各审计小组的进度及交流、研究审计中遇到的问题；⑪8月份继续进驻社区实施就地审计，进行审计取证；⑫8月22日召开各审计小组成员会议，了解各审计小组的进度、研究审计中遇到的问题；⑬9月上旬根据就地审计过程中收集回来的信息和审计资料，汇总整理24个被审计单位的审计工作底稿和《审计报告（征求意见稿）》；⑭9月18日召开各审计小组成员会议，会议对各审计小组审计报告（征求意见稿）提出审计问题进行分析研究，并要求应严格落实每个被审单位审计终结阶段的文书处理工作；⑮9月20日将《审计报告（征求意见稿）》下发至被审计单位征求其意见；⑯9月29日收回被审计单位《审计报告认定书》；⑰10月初，梳理《回复意见》，拟出《审计报告》报领导审批；⑱10月28日将《审计报告》下发至被审计单位，并要求其对审计中发现的问题进行整改；⑲对审计查出问题进行全面整改并定期检查其整改落实情况；⑳对整改完毕单位的审计资料进行归档。', null, '无。', 'A', null, null, '', '0.00', '无。', '100', '完成', 'Y', 'N', 'O', null, '2017-01-13 18:47:29', '王永安', '2016', '12');
