INSERT INTO `wt_task_execution` VALUES ('41213709f1f94211aeb73f1619f2b790', '201603062', 'b8836f36dde244608e6970fba4dbc7ef', '319528c74cd44013a3573995fe0c0227', '2017-01-03 16:19:22', '富都商业大厦和宿舍楼项目已另设条件进行投资招标。\r\n商业广场智能停车场项目调整方案已通过班子联席会议审议，新方案预算造价正由财政分局审核，财审报告尚未出具。', null, '暂无', 'A', null, null, '', '0.00', '富都商业大厦和宿舍楼项目按相关程序办理及跟进。\r\n商业广场智能停车场项目待财政审核报告出具后申请办理公开招标手续。', '40', '正常', 'Y', 'N', 'O', null, null, null, '2016', '12');
