INSERT INTO `wt_task_execution` VALUES ('6a5b17d73f5f4c58869d0575ae37505c', '201602054', 'a25a5dbaec0c42c68d9129bbdcb281d4', '319528c74cd44013a3573995fe0c0169', '2017-01-09 17:00:55', '    1、加大对虹桥市场及其周边道路城市“六乱”整治工作，重点对虹桥市场周边道路沿街商铺占道经营、乱拉乱挂、使用高音喇叭招揽顾客等违法行为进行查处，2016年，我分局共出动719人次，查处乱摆卖1321宗、占道经营931宗，查处乱拉乱挂85宗。\r\n    2、我分局多次会同镇工商分局、食药监分局、公安分局、竹溪社区以及厚街社区对虹桥市场与首信市场周边巷道的占道经营行为开展联合整治行动，对占道经营行为的当事人进行了批评教育并暂扣物品。\r\n    3、我分局在虹桥市场周边沿街店铺门前划定黄线作为其经营区域，要求各店铺在黄线以外不得摆放任何经营物品，“黄线工程”在制止店铺占道经营方面取得显著成效，划线后占道经营现象大大减少。', null, '无', 'A', null, null, '', '0.00', '下来，我分局将进一步加强对虹桥市场周边区域的巡查和监管力度。', '100', '正常', 'Y', 'N', 'O', null, '2017-01-09 17:01:16', '曾建星', '2016', '12');
