INSERT INTO `wt_task_execution` VALUES ('7ddc791cd5134efdaa1e5b8a7d3fdbd2', '201602085', '17506108853c4cd3b1e4787e78fe907e', '319528c74cd44013a3573995fe0c0135', '2016-12-30 18:24:28', '①4月底开始对全镇各社区廉政风险预警防控机制的检查，目前已完成所有社区的检查；②4月底按照市预防办的部署，对我镇全市第一批预防职务犯罪示范村（居）开展“回头看”工作检查；③已推荐白濠社区创建全市第二批预防职务犯罪工作示范村，并于6月27日完成验收检查工作，8月17日被授予第二批东莞市“预防职务犯罪工作示范村（社区）”称号；④下社区开展调研，同时就前期检查发现问题指导并督促有关社区进行整改；⑤实施东莞市政府工程廉情预警评估系统试点工作；⑥2016年12月23日，完成前期检查发现问题的整改工作。', null, '无。', 'A', null, null, '', '0.00', '无。', '100', '完成', 'Y', 'N', 'O', null, '2017-01-13 19:11:21', '王永安', '2016', '12');
INSERT INTO `wt_task_execution` VALUES ('7e945a73648942a786b814ef115e8cdc', '201604061', '5aa6ada9d52e4edca19883c69c34114e', 'c68c9cfeed3c42f4b18ea5d81754d66a', '2017-01-08 16:32:17', '由于今年双岗社区进行全面更换地下水管工程和家具大道桥重建工程，需要进行大量的挖掘地面工作，若社区在此时加装治安视频，容易被工程挖段线路。故加装治安视频工作待上述两项工程完工后再装。', null, '暂无', 'A', null, null, '', '0.00', '由于今年双岗社区进行全面更换地下水管工程和家具大道桥重建工程，需要进行大量的挖掘地面工作，若社区在此时加装治安视频，容易被工程挖段线路。故加装治安视频工作待上述两项工程完工后再装。', '100', '完成', 'Y', 'N', 'O', null, null, null, '2016', '12');
