INSERT INTO `wt_task_execution` VALUES ('8d778e9c5982493e9f2d9d4f2de6f0a8', '201602030', '05e0865b50c24bba902774cef52e3794', '319528c74cd44013a3573995fe0c0120', '2017-01-13 15:35:49', '目前，已协助新泽模具、丸仁电子等8家外商企业申报2016年东莞市企业成长培育专项资金奖励。推动时代皮具公司获得市信息化专项资金资助，推动远梦公司和名纳居公司被评为市电子商务示范企业。同时，选取我镇出口超1000万元外资企业作为“规上”工业企业重点培育名单，争取推动一批企业上规模，发展壮大。', null, '无', 'A', null, null, '', '0.00', '做好全镇外资企业培育引导工作，积极挖掘一批“专精特”和“小上规”等优质后备企业，辅导培育发展为“上规”企业。', '100', '完成', 'Y', 'N', 'O', null, null, null, '2016', '12');
INSERT INTO `wt_task_execution` VALUES ('8ee460386fde4a70b5e42a138324a3eb', '201701062', '8d3eed14a3e04300970e195d4cd4b4d5', '319528c74cd44013a3573995fe0c0237', '2017-02-28 16:36:21', '根据班子会议讨论结果，同意新塘社区承光厂租地建厂事宜\r\n    关于承光厂新地分社及下塘分社拆除原来厂房及宿舍建筑物事宜立项已完成。\r\n    现时我社区已与承光厂签订如下租地建厂合同：\r\n    1.新塘经联社原邰富厂租地建厂合同\r\n    2.下塘分社原捷瑞厂租地建厂合同\r\n    而承光厂新地分社的租地建厂合同还在进行合同起草及修改。', null, '无', 'A', null, null, '', '0.00', '我社区尽快与司法所就承光厂新地分社租地建厂事宜做好合同修改并尽快签订租赁合同。', '60', '正常', 'Y', 'N', 'O', null, null, null, '0', '0');
