INSERT INTO `wt_task_execution` VALUES ('c0faa273257f4869992f00f4921332d2', '201603179', '4a4714878dff4921952e42219356ff6f', '693e3ec07c4e483f96463ea3c9f77452', '2017-01-05 19:13:45', '1、完成财务管理风险排查工作。                              2、开展土地清算工作。                                                    3、与社保分局进行联合执法，将达到重点欠费户标准的企业移送法院强制执行，清理社保欠', null, '暂无', 'A', null, null, '', '0.00', '1、与社保分局进行联合执法，将达到重点欠费户标准的企业移送法院强制执行，清理社保欠费。                           2、做好2016年全年重点税收优惠政策落实情况统计工作。                                                                                                                  3、创建厚街地税综合管理系统                       4、继续加强征管，挖潜补漏。', '100', '正常', 'Y', 'N', 'O', null, null, null, '2016', '12');
INSERT INTO `wt_task_execution` VALUES ('c127e16a54464b308bf07b35ec8dd453', '201602060', '0402ad1d999e4db9a3c794128fa633a1', '319528c74cd44013a3573995fe0c0143', '2017-01-05 09:44:08', '\"1.每月向镇领导及风险预警系统成员单位报送《厚街镇企业风险预警应急系统管理情况简报》， 要求成员单位对欠款企业进行督缴，各社区密切留意企业动态。\r\n2. 5月3日，镇政府出台《厚街镇人民政府关于建立行政司法联动打击恶意欠薪长效机制的工作意见》（厚府〔2016〕19号），建立拒不支付劳动报酬犯罪举报', null, '暂无', 'A', null, null, '', '0.00', '\"1.不断推进劳动关系风险预警系统建设，创新管理方式，开启将风险隐患主体作为我镇“大综管”平台的列管对象，借力社区网格化治理，大大提高欠薪隐患预警防范力度。\r\n2.继续加强与行政司法联动打击恶意欠薪工作领导小组成员单位信息共享和问题研判，确保无缝衔接，工作做好恶意欠薪综合治理工作，保障广大劳动者合法工资权益。\r\n3.继续贯彻日常巡查与专项行动相结合的工作思路，依法严肃查处违法行为。\"\r\n', '100', '正常', 'Y', 'N', 'O', null, null, null, '2016', '12');
