INSERT INTO `wt_worktask` VALUES ('175021', '7c9b11e6f8854791a15f4672c75be374', '175021', '推进教育信息化工程', '1.完善慕课、翻转课堂硬件条件；                                  2.以双岗小学为智慧校园抓手，推进全镇公民学校打造智慧校园，探索智慧课堂，提高教育教学质量；                          3.建立教育资源大数据库，共享优质教育教学资源。', 'A', '0', 'A', null, '', null, '1118774c5c44423582f8635484318111', '2017-03-01 00:00:01', '', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '0', '0', '175021', null, null, '正常', '0', 'N', 'N', 'B', null);
