INSERT INTO `wt_worktask` VALUES ('175026', '7c9b11e6f8854791a15f4672c75be374', '175026', '备战东莞市第九届运动会', '    东莞市第九届运动会将于2017年4月至9月期间举行，我镇将组团参赛并力争进入总分前八名。工作内容：一.做好市运会形势和各项目竞赛规程分析研究，加强与各业训单位特别是学校的联系与沟通，制定备战和参赛工作方案；二.抓好我镇击剑、飞碟射击等优势项目及其他项目的备战工作，提高核心竞争力；三. 明确目标任务，落实训练和参赛所需的场地、经费、人员等后勤服务工作；四.做好我镇承办市运会击剑、飞碟射击、射箭、篮球等赛区筹备工作。', 'A', '0', 'A', null, '', null, '1118774c5c44423582f8635484318111', '2017-03-01 00:00:01', '', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '0', '0', '175026', null, null, '正常', '0', 'N', 'N', 'B', null);
