INSERT INTO `wt_worktask` VALUES ('175031', '7c9b11e6f8854791a15f4672c75be374', '175031', '继续实施百花工程      业余文艺团队扶持工作', '按照《厚街镇业余文艺团体扶持办法》及相关细则，向符合扶持条件的社会文艺团体发放扶持经费、文化惠民演出补贴及精品创作奖励，进一步促进社会文艺团体的发展壮大和各类社会文化的繁荣发展。', 'A', '0', 'A', null, '', null, '1118774c5c44423582f8635484318111', '2017-03-01 00:00:01', '', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '0', '0', '175031', null, null, '正常', '0', 'N', 'N', 'B', null);
INSERT INTO `wt_worktask` VALUES ('175032', '7c9b11e6f8854791a15f4672c75be374', '175032', '加强文物保护', '1、完成省级文物保护单位方氏宗祠保护规划编制及验收工作。2、协助开展桥头节度陈公祠、通政陈公祠，溪头隐斋公祠，环冈隐斋公祠4处文物的相关修缮工作。', 'A', '0', 'A', null, '', null, '1118774c5c44423582f8635484318111', '2017-03-01 00:00:01', '', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '0', '0', '175032', null, null, '正常', '0', 'N', 'N', 'B', null);
INSERT INTO `wt_worktask` VALUES ('175033', '7c9b11e6f8854791a15f4672c75be374', '175033', '建设厚街镇公交车电视               媒体信息发布平台', '计划与大众公交车公司合作，利用公交车电视媒体平台，播放每天的《厚街新闻》、《平安厚街》及各类公益广告，同时适当加入商业广告的投放。', 'A', '0', 'A', null, '', null, '1118774c5c44423582f8635484318111', '2017-03-01 00:00:01', '', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '0', '0', '175033', null, null, '正常', '0', 'N', 'N', 'B', null);
INSERT INTO `wt_worktask` VALUES ('175034', '7c9b11e6f8854791a15f4672c75be374', '175034', '开展以查处“涉黄”行为为重点的歌舞娱乐场所专项整治', '通过集中大行动、定期与不定期巡查相结合的方式开展拉网式检查，重点检查经营场所许可证、警示标志、有无营利性陪侍行为等。在巡查中，一旦发现娱乐场所存在嫌疑“涉黄”行为，立即通知当地警务人员作现场移交，并于两个工作日内发移交函到相关职能部门。同时，对有违法记录的文化经营场所进行重点检查和回访调查，确保长期有效监管。', 'A', '0', 'A', null, '', null, '1118774c5c44423582f8635484318111', '2017-03-01 00:00:01', '', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '0', '0', '175034', null, null, '正常', '0', 'N', 'N', 'B', null);
INSERT INTO `wt_worktask` VALUES ('175035', '7c9b11e6f8854791a15f4672c75be374', '175035', '实行网吧分级管理制度', '采取日常巡查、突查和暗访等方式对网吧进行检查，对三个月内检查未发现违法违规行为的网吧，未被相关部门处罚过的网吧，评为A级网吧；对三个月内检查发现有消费者吸烟、消防通道阻塞、未领取食品流通许可证而销售食品等轻微违规行为的网吧，评为B级网吧，网吧三月以内两次及两次以上违反上述规定，评为C级网吧；对三个月内发生有未按规定核对验证消费者身份证、擅自停止实施经营管理技术措施、未亮证经营等行为的网吧为C级，网吧三月以内两次及两次以上违反上述规定，评为D级网吧。对接纳未成年人的网吧，一律评为D级。对A、B级网吧，每月进行不少于两次巡查；对C级网吧，每月进行不少于三次巡查；对D级网吧每月检查四次以上。', 'A', '0', 'A', null, '', null, '1118774c5c44423582f8635484318111', '2017-03-01 00:00:01', '', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '0', '0', '175035', null, null, '正常', '0', 'N', 'N', 'B', null);
INSERT INTO `wt_worktask` VALUES ('175036', '7c9b11e6f8854791a15f4672c75be374', '175036', '扫黄打非工作', '深入开展“清源”、“净网”、“秋风”、“护苗”等专项行动，通过“扫摊”、“挖仓”、“断源 ”销毁非法出版物和“校园周边整治”等，坚决扫除文化市场和互联网上各种非法出版物和有害信息。', 'A', '0', 'A', null, '', null, '1118774c5c44423582f8635484318111', '2017-03-01 00:00:01', '', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '0', '0', '175036', null, null, '正常', '0', 'N', 'N', 'B', null);
