INSERT INTO `wt_worktask` VALUES ('175038', '7c9b11e6f8854791a15f4672c75be374', '175038', '提升引资质量，优化招商引资工作实效', '创新招商方法，加大招商工作力度。主动走出去招商，做好项目对接服务，提高在谈项目落户的成功率。继续发挥招商队伍作用，组织召开招商推介会等活动。挖掘一批优质意向项目建立招商引资项目信息库。发挥项目委托评估机构作用，确保项目质量，推动招商引资工作的持续发展。力争2017年实现引进外资项目（含增资）达30宗以上，合同外资金额1156万美元，同比增长2%；实际外资金额9978万美元，同比增长2%;引进1-2个大型项目落户。年内促成伯产皮革项目等新签项目正式投产运营，促成三星视界、润信弹性、远梦家居、威信运动和泰科电子等大企业实现增资扩产。', 'A', '0', 'A', null, '', null, '1118774c5c44423582f8635484318111', '2017-03-01 00:00:01', '', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '0', '0', '175038', null, null, '正常', '0', 'N', 'N', 'B', null);
