INSERT INTO `wt_worktask` VALUES ('175147', '7c9b11e6f8854791a15f4672c75be374', '175147', '2017年“村容村貌”升级改造工程招投标工作', '    根据镇委、镇政府对镇“村容村貌”升级改造工作的总体规划，2017年是“村容村貌”升级改造工作实施的第三年，根据前两年的经验，社区申请的升级改造项目循逐年增加的态势，2017年将会有更多的“村容村貌”升级改造工程办理投投标工作。\n    2017年我中心将按照镇委、镇政府的工作部署，认真贯彻落实工程招投标管理办法及相关法律法规，并根据《厚街镇进一步规范零星工程管理细则》厚府[2016]27号的相关规定开展招投标工作。重点协助社区做好“村容村貌”升级改造工程的招投标工作。', 'A', '0', 'A', null, '', null, '1118774c5c44423582f8635484318111', '2017-03-01 00:00:01', '', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '0', '0', '175147', null, null, '正常', '0', 'N', 'N', 'B', null);
INSERT INTO `wt_worktask` VALUES ('175148', '7c9b11e6f8854791a15f4672c75be374', '175148', '厚街社区集体资产交易工作', '    镇社资办对现行《厚街镇社区集体资产交易办法》部分条款进行了修改，修改后交易工作与之前有着较大的变化，如：取消社区交易点交易、新增网上交易方式、明确社区交易点的业务以网上交易方式代替、取消意向人报名资料中有关部门出具的客商证明等等。交易办法的修改，意味着接下来社区集体资产交易项目的数量、对竞投意向人资格审核工作等将大大增加。\n    2017年我中心将严格贯彻落实《厚街镇社区集体资产交易办法》的有关规定，按时、按质、按量完成经镇社资办审批同意的资产交易项目。', 'A', '0', 'A', null, '', null, '1118774c5c44423582f8635484318111', '2017-03-01 00:00:01', '', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '2017-03-01 00:00:01', '2017-12-31 23:59:59', '0', '0', '175148', null, null, '正常', '0', 'N', 'N', 'B', null);
