INSERT INTO `wt_worktask` VALUES ('201603102', '201603', '3102', '推进社区网格化管理工作。', '一是规范管理，进一步完善联合执法等工作机制，完善部门入格流程，定期梳理入格部门的入格事项，制定适合基层综管员执行的工作清单；\n二是加强队伍管理，严把综管员聘用门槛，落实综管员队伍培训工作常态化，优化综管员考核方案，实行优胜劣汰，全面提高综管员综合素质；\n三是完善系统功能，进一步完善基层社会治理网格信息平台的考核、通报、统计、分析等各项功能，提高平台系统的稳定性；\n四是加强大数据应用，全面采集建筑、人的基础信息，丰富网格信息平台数据库，通过大数据应用为基层社会治理提供更多有针对性的支持；\n五是延伸服务职能，推动更多职能入格，推进专业市场和楼盘共治工作，探索服务与管理“一体化”的新型基层治理模式。\n', '', '0', 'C', null, null, null, '1118774c5c44423582f8635484318111', '2016-12-01 08:00:00', null, '2016-12-01 00:00:01', '2016-12-31 23:59:59', null, null, '0', '96', '3102', null, null, '正常', null, 'N', 'N', 'B', '1');
