INSERT INTO `wt_worktask` VALUES ('201603182', '201603', '3182', '推进专业网格建设，探索“专业网格监管”模式。', '以进一步推进商改后续监管为突破口，联合镇社区网格化管理工作领导小组办公室针对前期未纳入社区综管办管理的专业市场，设立专业网格探索“专业网格监管”模式，根据专业网格列管对象的特点，将工商、消防、安监、公安等部门的职能整合纳入专业网格，确定具体的监管事项及内容，明确专业网格所涉社区及其综管员、部门、物管公司的具体分工和工作流程，并充分发挥专业场物管公司的自治作用，加强对场内市场主体的管理，形成社区、部门、企业多元共治格局。', '', '0', 'C', null, null, null, '1118774c5c44423582f8635484318111', '2016-12-01 08:00:00', null, '2016-12-01 00:00:01', '2016-12-31 23:59:59', null, null, '0', '100', '3182', null, null, '正常', null, 'N', 'N', 'B', '1');
INSERT INTO `wt_worktask` VALUES ('201603183', '201603', '3183', '推进“诉转案”工作', '转变以往处理消费投诉局限于以调解为主、解决单个消费者问题的方式，着重通过对每宗消费投诉认真核查违法线索，界定商家是否主观上存在欺诈消费者等行为，对违法行为进行及时查处，倒逼商家调整经营行为，制止侵害消费者权益行为的重复出现，从而减少同类投诉的发生，有效维护消费者的合法权益。要全面落实“诉转案”机制，明确“诉转案”具体流程，将“诉转案”作为解决群众消费维权“最后一公里”问题的重要举措，进一步维护民生利益。', '', '0', 'C', null, null, null, '1118774c5c44423582f8635484318111', '2016-12-01 08:00:00', null, '2016-12-01 00:00:01', '2016-12-31 23:59:59', null, null, '0', '100', '3183', null, null, '正常', null, 'N', 'N', 'B', '1');
