INSERT INTO `wt_worktask` VALUES ('201701044', '35788b4858e44370aed6e06cadccaef2', '201701044', '农林水务局《关于启动2015-2017年截污次支管网工程前期工作的请示》（镇〔2016〕1733号）', '审议并原则同意该请示，会议同时明确：（1）关于赤岭、双岗管网的衔接，由杜学民同志牵头环保分局、规划所、农林水务局研究确定。（2）要科学安排施工。一是围蔽要尽量减少对周边商户、居民的影响，二是招标条款要明确道路原标准修复。', 'B', '0', 'A', null, null, null, '1118774c5c44423582f8635484318111', '2017-02-01 00:00:01', null, '2017-02-01 00:00:01', '2017-02-28 23:59:59', null, null, '0', '56', '1044', null, null, '正常', null, 'N', 'N', 'E', '2017-02-28');
