INSERT INTO `wt_worktask` VALUES ('226f8856d6a34fc0b7184e4950030005', '201601', '201601005', '五、实施镇中心区环卫统筹工程', '实现对清扫保洁面积约423.96万平方米，清运垃圾量约6.57万吨/年，以及20座垃圾转运站和7座公厕的统筹管理。升级改造14座垃圾压缩转运站，规划新建5座垃圾转运站，升级改造5座公厕，规划新建2座公厕，新建18座环卫工人休息室。', 'B', '0', 'C', null, null, null, '1118774c5c44423582f8635484318111', '2016-12-01 08:00:00', null, '2016-12-01 00:00:01', '2016-12-31 23:59:59', null, null, '0', '0', '1005', null, null, '正常', null, 'N', 'N', 'B', '1');
INSERT INTO `wt_worktask` VALUES ('226f8856d6a34fc0b7184e4950030006', '201601', '201601006', '六、提升生态景观', '完成厚街体育休闲公园建设、松山公园升级改造及神仙水公园配套工程。\n厚街体育休闲公园工程：计划年内完成。\n松山公园升级改造工程：对公园内周边部分园建、绿化进行改造。\n神仙水公园配套工程：改造和增加南北两个入口（设置2个门楼），配套3个停车场共300度个停车位，改造景观绿化。', 'B', '0', 'C', null, null, null, '1118774c5c44423582f8635484318111', '2016-12-01 08:00:00', null, '2016-12-01 00:00:01', '2016-12-31 23:59:59', null, null, '0', '55', '1006', null, null, '正常', null, 'N', 'N', 'B', '1');
INSERT INTO `wt_worktask` VALUES ('226f8856d6a34fc0b7184e4950030007', '201601', '201601007', '七、整治虹桥市场周边交通', '完善市场周边交通疏导，升级周边道路及增加停车设施。', 'B', '0', 'C', null, null, null, '1118774c5c44423582f8635484318111', '2016-12-01 08:00:00', null, '2016-12-01 00:00:01', '2016-12-31 23:59:59', null, null, '0', '77', '1007', null, null, '正常', null, 'N', 'N', 'B', '1');
INSERT INTO `wt_worktask` VALUES ('226f8856d6a34fc0b7184e4950030008', '201601', '201601008', '八、加强食品安全监管', '加强食品药品安全网格化体系建设，力争年内建成食品药品安全网格化试点社区，逐步推广到全镇23个社区。同时强化食品安全快检快筛和监督抽检，力争年内完成快速检测100批，完成流通环节抽检100批，餐饮环节抽检90批，并定期发布食品抽检质量信息。', 'B', '0', 'C', null, null, null, '1118774c5c44423582f8635484318111', '2016-12-01 08:00:00', null, '2016-12-01 00:00:01', '2016-12-31 23:59:59', null, null, '0', '100', '1008', null, null, '完成', null, 'N', 'N', 'B', '1');
INSERT INTO `wt_worktask` VALUES ('226f8856d6a34fc0b7184e4950030009', '201601', '201601009', '九、推动就业创业', '推动高校毕业生、就业困难人员等重点群体实现就业创业，计划年内实现就业创业服务率达100%，高校毕业生就业率达96%以上；帮扶就业困难人员200人实现就业，并推动6个就业安置基地申办。借助省、市技能晋升补贴政策，联合镇电商协会、镇妇联、镇团委组织户籍青年、妇女参加“网商运营”、“电子商务师”的项目培训。搭建诸如“厚街最美工业产品展示中心”之类的集中展销平台，以镇财政租金补贴的形式推动企业厂家或户籍创业青年在展销平台开办实体店，在运行成熟的情况下，协助项目主体申请省、市创业孵化基地项目。', 'B', '0', 'C', null, null, null, '1118774c5c44423582f8635484318111', '2016-12-01 08:00:00', null, '2016-12-01 00:00:01', '2016-12-31 23:59:59', null, null, '0', '100', '1009', null, null, '完成', null, 'N', 'N', 'B', '1');
INSERT INTO `wt_worktask` VALUES ('226f8856d6a34fc0b7184e4950030010', '201601', '201601010', '十、提升社区卫生服务站硬件', '建1栋2层房屋建筑（岭南风格），占地302.36平方米，总建筑面积为562.24平方米。', 'B', '0', 'C', null, null, null, '1118774c5c44423582f8635484318111', '2016-12-01 08:00:00', null, '2016-12-01 00:00:01', '2016-12-31 23:59:59', null, null, '0', '88', '1010', null, null, '完成', null, 'N', 'N', 'B', '1');
INSERT INTO `wt_worktask` VALUES ('494a1d9a45a04331a038c1604139cf0c', '1e5a0a5408ce479dbaf481989fa45fe4', '0116100413', '一、海逸高尔夫球场整治工作', '由农林水务局、环保分局抓好落实海逸高尔夫球场监督管理工作。一是督促海逸高尔夫球场整改措施到位；二是严格按照整改措施运营，确保收集池等设施正常使用、企业正常运营；三是既要加强海逸高尔夫球场的巡查，也要加强周边其他企业的巡查，避免因其他企业的问题对海逸高尔夫球场造成影响。', 'A', '0', 'C', null, '杜学民', null, '11ce2b281a314db3aa6e7dcfb4a41077', '2017-01-16 10:04:13', null, '2017-01-01 10:03:00', '2017-12-31 10:03:00', null, null, '0', '100', '171001', null, null, '完成', '0', 'N', 'N', 'A', '28');
INSERT INTO `wt_worktask` VALUES ('4ca78cab432940cdbe119f66182b22fe', '1e5a0a5408ce479dbaf481989fa45fe4', '0116094303', '四、截污管网建设', '一是按照方案要求，全面协调各方，加快截污次支管网19-20号#工程建设，并尽快启动二期工程建设，确保进度正度推进；二是对于截污管网设计问题，要加强与设计单位沟通，明确指出问题，对下期因设计错误增加的费用由设计单位承担；三是由水务建运中心就树管养、管网建设协调问题书面报告市环保局，请求市环保局协调，同时，就树管养、管网建设协调问题等相关情况书面发函市城管局，并要求限期回复。', 'A', '0', 'A', null, '杜学民', null, '11ce2b281a314db3aa6e7dcfb4a41077', '2017-01-16 09:43:03', null, '2017-01-01 09:42:00', '2017-12-31 09:42:00', null, null, '0', '70', '171007', null, null, '正常', '0', 'N', 'N', 'A', '28');
INSERT INTO `wt_worktask` VALUES ('b086af40fc05452e8f87adb4d5d700de', '1e5a0a5408ce479dbaf481989fa45fe4', '0116100219', '二、镇重点环境问题整治工作（2）', '石角码头沙场整治工作由杜学民委员牵头，制定联合执法预案，组织水务、环保、工商、城管、公安、国土、住建、供电、供水等部门对石角码头沙石场所进行全面整治。一是对石角码头无牌无证沙石场所进行执法查处；二是对石角码头沙石场所的违法建筑进行强制拆除；三是对石角码头沙石场所进行断电、断水；四是对石角码头违反用地手续的沙石场所进行清理，责令复耕复农；五是加固石角码头周边堤围建设。', 'A', '0', 'C', null, '李惠勤,陈锦胜,潘建军,杜学民,刘创胜,方沛林,陈应新', null, '11ce2b281a314db3aa6e7dcfb4a41077', '2017-01-16 10:02:19', null, '2017-01-01 09:55:00', '2017-12-31 09:56:00', null, null, '0', '82', '171003', null, null, '完成', '0', 'N', 'N', 'A', '28');
INSERT INTO `wt_worktask` VALUES ('eecd7270627c410396f9674edd93e84a', '1e5a0a5408ce479dbaf481989fa45fe4', '0116100319', '二、镇重点环境问题整治工作（1）', '凯伦花园噪音问题由镇房管所牵头落实。全面加大整治力度，督促凯伦花园物业、开发商落实噪音污染防治主体责任，加快隔音板工程建设，并在12月底前以镇政府名义向凯伦花园物业、开发商发函，函告相关整治要求及责任后果。', 'A', '0', 'A', null, '方沛林', null, '11ce2b281a314db3aa6e7dcfb4a41077', '2017-01-16 10:03:19', null, '2017-01-01 10:02:00', '2017-12-31 10:02:00', null, null, '0', '66', '171002', null, null, '正常', '0', 'N', 'N', 'A', '28');
INSERT INTO `wt_worktask` VALUES ('f756654f5ede4c8fabbdaa3bed8b3d3f', '1e5a0a5408ce479dbaf481989fa45fe4', '0116095511', '二、镇重点环境问题整治工作（3）', '由环保分局起草环境保护属地责任管理办法，采取有效、坚决措施，促使环保管理到位。要求：一是尽快落实环保网格监管，二是全面加强重点区域、重点企业的监管，三是采取相应措施推动环保管理，如：落实社区属地管理职责，建立环保问责机制等。', 'A', '0', 'C', null, '杜学民', null, '11ce2b281a314db3aa6e7dcfb4a41077', '2017-01-16 09:55:11', null, '2017-01-01 09:54:00', '2017-12-31 09:54:00', null, null, '0', '100', '171004', null, null, '完成', '0', 'N', 'N', 'A', '28');
