  `schedule` int(3) NOT NULL DEFAULT '0' COMMENT '本次进度',
  `schedule_description` varchar(10) DEFAULT '正常' COMMENT '正常、较慢、完成、已办结、未办结',
  `approval_status` varchar(1) NOT NULL DEFAULT 'O' COMMENT 'O审批中Y通过N不通过',
  `is_lock` varchar(1) DEFAULT 'N' COMMENT 'Y已锁定N未锁定',
  `is_back_finish` varchar(1) DEFAULT 'O' COMMENT 'O正常N退回Y已整理',
  `opt_reason` text COMMENT '原因描述（退回/不通过）',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `update_user` varchar(32) DEFAULT NULL COMMENT '更新人',
  `submit_year` int(4) DEFAULT '0' COMMENT '提交年份',
  `submit_time` int(2) DEFAULT '0' COMMENT '提交阶段',
  PRIMARY KEY (`task_execution_id`)
